mapper.select:
select count(1)
from bi_bills_${yearMonth}

	
		user_id = #{userId}
	
	and consumer_type = 2

select id as id, phone_no as phoneNo,
package_id as packageId, package_name as packageName,
is_group_user as isGroupUser, network as network, status as status, contract_from as contractFrom, contract_to as contractTo, regular_score as regularScore, value_change as valueChange from bi_bills_${yearMonth}
user_id = #{userId}
and phone_no = #{phoneNo}
and consumer_type = 2
order by gprs_spill desc limit
#{pageBegin}, #{pageSize}
50

select count(1)
from bi_bills_${yearMonth}

 	
 		user_id = #{userId}
 	
 	
   and contract_to < #{maxContract}
   
   
   and contract_to > #{minContract}
   
   
   and contract_to > 0

select id as id, phone_no as phoneNo,
package_id as packageId, package_name as packageName,
is_group_user as isGroupUser, network as network, status as status, contract_from as contractFrom, contract_to as contractTo, regular_score as regularScore, priority as priority, value_change as valueChange from bi_bills_${yearMonth}
user_id = #{userId}
and phone_no = #{phoneNo}
and contract_to < #{maxContract}
and contract_to > #{minContract}
and contract_to > 0
order by ${orderField} desc limit
#{pageBegin}, #{pageSize}
50

select id as id, phone_no as phoneNo, the_month as yearMonth, package_id as packageId, package_name as packageName, ROUND(income,2) as income, monthly_rental as monthlyRental, local_voice_fee as localVoiceFee, roaming_fee as roamingFee, long_distance_voice_fee as longDistanceVoiceFee, value_added_fee as valueAddedFee, other_fee as otherFee, grant_fee as grantFee, call_number as callNumber, local_call_duration as localCallDuration, roam_call_duration as roamCallDuration, long_distance_call_duration as longDistanceCallDuration, internal_call_duration as internalCallDuration, international_call_duration as internationlCallDuration, sms as sms, ROUND(gprs,2) as gprs, is_group_user as isGroupUser, network as network, status as status, contract_from as contractFrom, contract_to as contractTo, consumer_type as consumerType, ROUND(income6,2) as income6, ROUND(income3,2) as income3, voice6 as voice6, voice3 as voice3, ROUND(gprs6,2) as gprs6, ROUND(gprs3,2) as gprs3, ROUND(package_spill,2) as packageSpill, ROUND(voice_spill,2) as voiceSpill, ROUND(gprs_spill,2) as gprsSpill, ROUND(income_fluctuation,2) as incomeFluctuation, ROUND(voice_fluctuation,2) as voiceFluctuation , ROUND(gprs_fluctuation,2) gprsFluctuation , ROUND(score,2) as score, recommend as recommend, ROUND(regular_score,2) as regularScore, priority as priority, value_change as valueChange from bi_bills_${yearMonth}
user_id = #{userId}
and phone_no = #{phoneNo}
limit 1
